import React, { Component } from "react";
import Mintlogo from "../../assets/images/group.svg";
import Container from "react-bootstrap/Container";

class Correctinfo extends Component {
  state = {
    default: false,
  };
  render() {
    const Showmoreoptions = () => {
      return (
        <>
          <Container>
            <div className="chat-thread-right">
              <p>Activity points are selected</p>
            </div>

            <div className="mint-pro-logo">
              <img src={Mintlogo} alt="logo" />
              <p>Mint Pro</p>
            </div>
            <div className="chat-thread-left">
              <p>Please provide more details about your issue</p>
            </div>
          </Container>
          <div className="input-wrapper">
            <Container fluid>
              {/* <input type="text" /> */}
              <Row>
                <Form>
                  <Form.Group>
                    <Form.Control
                      size="sm"
                      type="text"
                      placeholder="Type details of issue"
                      required
                      onChange={this.activaButton}
                      // id="focusInput"
                    />
                  </Form.Group>
                  <Button type="submit" className="submit-btn">
                    <img src={Sendicon} alt="send icon" />
                  </Button>
                </Form>
              </Row>
            </Container>
          </div>
        </>
      );
    };

    const Rendermoreoptions = () => {
      const checkstateStatus = this.state.default;
      if (checkstateStatus) {
        return <Showmoreoptions />;
      }
      return null;
    };

    return (
      <>
        <Container>
          <div className="chat-thread-right">
            <p>Yes, it looks correct</p>
          </div>

          <div className="mint-pro-logo">
            <img src={Mintlogo} alt="logo" />
            <p>Mint Pro</p>
          </div>
          <div className="chat-thread-left">
            <p>That's great. Do you have any other questions?</p>
            <ul className="options">
              <li onClick={this.yes}>Yes, I have more question.</li>
              <li onClick={this.no}>No, I don't have any more questions.</li>
            </ul>
          </div>
        </Container>
      </>
    );
  }
}

export default Correctinfo;
