import React from "react";
import PropTypes from "prop-types";
import Navicon from "../../assets/images/navigate.svg";
import Searchicon from "../../assets/images/search.svg";

const Navbar = () => {
  function showInput() {
    let iconWrapper = document.querySelector(".search-input");
    iconWrapper.style.display = "block";
    iconWrapper.focus();
  }
  const hideInput = () => {
    let iconWrapper = document.querySelector(".search-input");
    iconWrapper.style.display = "none";
  };
  return (
    <section>
      <div className="navbar-wrapper">
        <div>
          <img src={Navicon} alt="Nav Icon" onClick={hideInput} />
        </div>
        <div className="icon-wrapper">
          <h1>Activity Points</h1>
          <img src={Searchicon} alt="Search Icon" onClick={showInput} />
        </div>
        <input
          type="text"
          className="search-input"
          placeholder="Search by name, points or description"
        />
      </div>
    </section>
  );
};
Navbar.propTypes = {
  onClickBackButton: PropTypes.func,
  title: PropTypes.string,
  onClickOpenSearchIcon: PropTypes.func,
  onChangeSearchText: PropTypes.func,
  searchText: PropTypes.string,
  onClickClosingSearchIcon: PropTypes.func,
  isSearch: PropTypes.bool,
};

export default Navbar;
