import React, { Component } from "react";
import Button from "react-bootstrap/Button";
import Incorrectinfo from "../incorrect-info";
import Correctinfo from "../correct-info";

class Activitydetails extends Component {
  state = {
    wrongInfo: false,
  };
  showIncorrectSection = () => {
    // const showList = this.state.wrongInfo;
    this.setState({
      wrongInfo: true,
    });
  };

  render() {
    const Showcontent = () => {
      return (
        <Incorrectinfo
          option={[
            {
              options: "Activity Points are incorrect",
            },
            {
              options: "Have not received the payout",
            },
            {
              options: "Need help with the calculation",
            },
            {
              options: "Some other reasons",
            },
          ]}
        />
      );
    };

    const Renderedcontent = (props) => {
      const check = this.state.wrongInfo;
      if (check) {
        return <Showcontent />;
      }
      return null;
    };
    return (
      <>
        <div className="chat-thread-left">
          <p>Do the details of activity points looks correct to you?</p>
          <div className="button-wrapper">
            <Button onClick={this.showIncorrectSection}>No</Button>
            <Button onClick={this.showCorrectSection}>Yes</Button>
          </div>
        </div>
        <Renderedcontent />
        <Correctinfo />
      </>
    );
  }
}

export default Activitydetails;
