import React, { Component } from "react";
import Button from "react-bootstrap/Button";
import Incorrectinfo from "../incorrect-info";
import Correctinfo from "../correct-info";
import Mintlogo from "../../assets/images/group.svg";

class Activitydetails extends Component {
  state = {
    wrongInfo: false,
    correctInfo: false,
    moreDetails: false,
  };
  showIncorrectSection = () => {
    this.setState({
      wrongInfo: true,
      correctInfo: false,
    });
  };
  showCorrectSection = () => {
    this.setState({
      moreDetails: true,
    });
  };

  showMoreOptions = () => {
    this.setState({
      correctInfo: true,
      wrongInfo: false,
    });
  };

  render() {
    const Showcontent = () => {
      return (
        <Incorrectinfo
          option={[
            {
              options: "Activity Points are incorrect",
            },
            {
              options: "Have not received the payout",
            },
            {
              options: "Need help with the calculation",
            },
            {
              options: "Some other reasons",
            },
          ]}
        />
      );
    };

    const ShowCorrectContent = () => {
      return <Correctinfo />;
    };

    const Renderedcontent = () => {
      const check = this.state.wrongInfo;
      if (check) {
        return <Showcontent />;
      }
      return null;
    };

    const RenderCorrectContent = () => {
      const checkContent = this.state.correctInfo;
      if (checkContent) {
        return <ShowCorrectContent />;
      }
      return null;
    };

    const showMore = ({ showMoreOptions }) => {
      return <h1>hello</h1>;
    };

    const showMoreQuery = ({ showMoreOptions }) => {
      const checkState = this.state.moreDetails;
      if (checkState) {
        return <h1>Hello</h1>;
      }
    };

    return (
      <>
        <div className="chat-thread-left">
          <p>Do the details of activity points looks correct to you?</p>
          <div className="button-wrapper">
            <Button onClick={this.showIncorrectSection}>No</Button>
            <Button onClick={this.showCorrectSection}>Yes</Button>
          </div>
        </div>
        <Renderedcontent />
        <RenderCorrectContent />
        <showMore />
      </>
    );
  }
}

export default Activitydetails;
